import React from "react";
import styled from "styled-components";
import Hamburger from "./hamburger";
import posed from "react-pose";

const LargeMenu = styled.div`
  display: flex;
  justify-content: space-around;
  flex-direction: row;
  align-items: center;
  height: 100%;
  @media screen and (max-width: ${props => props.size}px) {
    display: none;
  }
  width: 100vw;
  background-color: #242526;
  opacity: 0.7;
  box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19);
  z-index: 2;
`;

const SmallMenu = styled(LargeMenu)`
  display: none;
  justify-content: space-between;
  @media screen and (max-width: ${props => props.size}px) {
    display: flex;
  }
  z-index: 2;
`;

const Container = styled.div`
  position: fixed;
  top: 0px;
  height: 55px;
  z-index: 1;
`;

const NavItem = styled.a`
  color: #f2f5f9;
  font-family: "Raleway", sans-serif;
  text-transform: uppercase;
  letter-spacing: 0.4em;
  margin: 25px;
  text-decoration: none;
  flex: 1;
  text-align: center;
`;

const NavItemMain = styled(NavItem)`
  font-size: 23px;
`;

const MobileMenuStyle = styled.div`
  overflow-y: auto;
  transition: transform 0.3s cubic-bezier(0.17, 0.67, 0.83, 0.67);
  width: 100vw;
  height: 100vh;
  background-color: #242526;
  position: fixed;
  top: 0;
  left: 0;
  z-index: 5;
  display: flex;
  justify-content: start;
  align-items: center;
  flex-direction: column;
`;

const MobileItemStyle = styled.a`
  color: #f2f5f9;
  text-align: center;
  width: 100%;
  font-size: 300%;
  margin: 40px;
  font-family: "Raleway", sans-serif;
  font-weight: 800;
  text-decoration: none;
`;

const MobileMenu = posed(MobileMenuStyle)({
  visible: {
    x: 0,
    y: "0vh",
    delayChildren: 600,
    staggerChildren: 100
  },
  hidden: {
    x: 0,
    y: "100vh"
  },
  initialPose: "hidden"
});

const MobileItem = posed(MobileItemStyle)({
  visible: {
    y: 0,
    transition: "easeIn"
  },
  hidden: {
    y: "100vh"
  }
});

class Header extends React.Component {
  state = {
    smallMenuOpen: false
  };

  openCloseMenu(val) {
    this.setState({ smallMenuOpen: val });
    //this.props.fullPageApi(!val);
  }

  getMainItem() {
    for (let i = 0; i < this.props.items.length; i++) {
      if (this.props.items[i].main) {
        return this.props.items[i];
      }
    }
  }

  render() {
    return (
      <Container>
        <LargeMenu size={980}>
          {this.props.items.map(item => {
            if (item.main) {
              return (
                <NavItemMain key={item.id} href={item.link}>
                  {item.title}
                </NavItemMain>
              );
            } else {
              return (
                <NavItem key={item.id} href={item.link} onClick={item.onClick}>
                  {item.title}
                </NavItem>
              );
            }
          })}
        </LargeMenu>
        <SmallMenu size={980}>
          <NavItemMain
            href={this.getMainItem().link}
            style={{ textAlign: "left" }}
          >
            {this.getMainItem().title}
          </NavItemMain>
          <Hamburger
            smallMenuOpen={this.state.smallMenuOpen}
            setOpen={val => this.openCloseMenu(val)}
          />
          <MobileMenu
            id="mobile-menu"
            pose={this.state.smallMenuOpen ? "visible" : "hidden"}
          >
            {this.props.items.map(
              item =>
                !item.main && (
                  <MobileItem
                    key={item.id}
                    href={item.link}
                    onClick={() => this.openCloseMenu(false)}
                  >
                    {item.title}
                  </MobileItem>
                )
            )}
          </MobileMenu>
        </SmallMenu>
      </Container>
    );
  }
}

export default Header;
